  return(sqrt((3*x^2 - 3*x + 2)/2));
}

real lowerhyper (real x) {
  return(-sqrt((3*x^2 - 3*x + 2)/2));
}

int i;

for (i = -3; i <= 3; ++i) {
  draw((-3,i)--(3,i),gray(0.7));
	draw((i,-3)--(i,3),gray(0.7));
}

